import { PieceType, TeamType } from "../components/Chessboard/Chessboard";

export default class Referee {

    tileIsOccupied

    isValidMove(px: number, py: number, x: number, y: number, type: PieceType, team: TeamType) {
        console.log("Ref is checking move");
        console.log(`"Previous Location: ${px} ${py}`);
        console.log(`"Current Location: ${x} ${y}`);
        console.log(`Piece Type: ${type}`);
        console.log(`Team: ${team}`);

        if(type === PieceType.PAWN){
            // moves for the white pawns
            if(team === TeamType.OUR){
                if(py === 1){
                    if(px === x && (y - py === 1 || y - py === 2)){
                        return true;
                    }
                }
                else {
                    if(px === x && (y - py === 1)){
                        return true;
                    }
                }
            }
            // pawn moves for black pawns
            else {
                if(py === 6){
                    if(px === x && (y - py === -1 || y - py === -2)){
                        return true;
                    }
                }
                else {
                    if(px === x && (y - py === -1)){
                        return true;
                    }
                }
            }

        }
        return false;
    }
}
