                if(desiredPosition.x < initialPosition.x && desiredPosition.y === initialPosition.y) {
                    let passedPosition: Position = {x: initialPosition.x - i, y: initialPosition.y};
                    // check if tile is destination tile
                    if(passedPosition.x === desiredPosition.x && passedPosition.y === desiredPosition.y){
                        return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
                    }
                    // passing tile
                    else{
                        if(this.tileIsOccupied(passedPosition, boardState)){
                            break;
                        }
                    }
                }

                // backwards
                if(desiredPosition.x === initialPosition.x && desiredPosition.y < initialPosition.y) {
                    let passedPosition: Position = {x: initialPosition.x, y: initialPosition.y - i};
                    // check if tile is destination tile
                    if(passedPosition.y === desiredPosition.y){
                        return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
                    }
                    // passing tile
                    else{
                        if(this.tileIsOccupied(passedPosition, boardState)){
                            break;
                        }
                    }
                }
            }

            return false;
        }
        else if(type === PieceType.KING){
            // Move 1 in any direction then if the rook is still in the position and the path is clear allow castling
            // Also king cannot move into a check and cannot castle if it has moved and passes through a check
        }


        return false;
    }
