import React from 'react';
import Tile from '../Tile/Tile';
import './Chessboard.css';
import{ useRef } from 'react';

const verticalAxis = ["1", "2", "3", "4", "5", "6", "7", "8"];
const horizontalAxis = ["a", "b", "c", "d", "e", "f", "g", "h"];

interface Piece {
    image: string;
    x: number;
    y: number;
}

const pieces: Piece[] = [];

for(let p = 0; p < 2; p++){
    const type = (p === 0) ? "b" : "w";
    const y = (p === 0) ? 7 : 0;
    pieces.push({image: `assets/images/rook_${type}.png`, x: 0, y: y});
    pieces.push({image: `assets/images/rook_${type}.png`, x: 7, y: y});
    pieces.push({image: `assets/images/knight_${type}.png`, x: 1, y: y});
    pieces.push({image: `assets/images/knight_${type}.png`, x: 6, y: y});
    pieces.push({image: `assets/images/bishop_${type}.png`, x: 2, y: y});
    pieces.push({image: `assets/images/bishop_${type}.png`, x: 5, y: y});
    pieces.push({image: `assets/images/bishop_${type}.png`, x: 2, y: y});
    pieces.push({image: `assets/images/bishop_${type}.png`, x: 5, y: y});
    pieces.push({image: `assets/images/queen_${type}.png`, x: 3, y: y});
    pieces.push({image: `assets/images/king_${type}.png`, x: 4, y: y});
}



for(let i = 0; i < 8; i++){
    pieces.push({image: "assets/images/pawn_b.png", x: i, y: 6});
}

for(let i = 0; i < 8; i++){
    pieces.push({image: "assets/images/pawn_w.png", x: i, y: 1});
}




export default function Chessboard() {
    const chessboardRef = useRef<HTMLDivElement>(null);


    let activePiece: HTMLElement | null = null;

    function grabPiece(e: React.MouseEvent<HTMLDivElement, MouseEvent>){
        const element = e.target as HTMLElement;
        if(element.classList.contains("chess-piece")){
            console.log(e);

            const x = e.clientX - 40;
            const y = e.clientY - 40;
            element.style.position = "absolute";
            element.style.left = `${x}px`;
            element.style.top = `${y}px`;

            activePiece = element;

        }
    }

    function movePiece(e: React.MouseEvent){
        const element = e.target as HTMLElement;
        const chessboard = chessboardRef.current;
        if(activePiece && chessboard){
            const minX = chessboard.offsetLeft - 25;
            const minY = chessboard.offsetTop -25;

            const maxX = chessboard.offsetLeft + chessboard.clientWidth - 50;
            const maxY = chessboard.offsetTop + chessboard.clientHeight - 50;

            const x = e.clientX - 40;
            const y = e.clientY - 40;

            activePiece.style.position = "absolute";

            // if x is smaller than min
            if (x < minX) {
                activePiece.style.left = `${minX}px`;
            }
            // if x is greater than max
            else if(x > maxX) {
                activePiece.style.left = `${maxX}px`;
            }
            // if x within constraints
            else {
                activePiece.style.left = `${x}px`;
            }

            // if y is smaller than min
            if (y < minY) {
                activePiece.style.top = `${minY}px`;
            }
            // if y is greater than max
            else if(y > maxY) {
                activePiece.style.top = `${maxY}px`;
            }
            // if y is within constraints
            else {
                activePiece.style.top = `${y}px`;
            }

        }
    }

    function dropPiece(e: React.MouseEvent) {
        if(activePiece) {
            pieces[0].x = 5;
            activePiece = null;
        }
    }


    let board = [];

        for(let j = verticalAxis.length - 1; j >= 0; j--){
            for(let i = 0; i < horizontalAxis.length; i++){
                const number = j + i + 2;
                let image = "";
                pieces.forEach(p => {
                    console.log(i, j);
                    if(p.x === i && p.y === j){
                        image = p.image;
                    }
                  });
                board.push(<Tile key={`${i},${j}`} image={image} number= {number} />);

        }
    }
    return <div 
        onMouseMove={e => movePiece(e)} 
        onMouseDown={e => grabPiece(e)} 
        onMouseUp={e => dropPiece(e)}
        id="chessboard"
        ref = {chessboardRef}
    >
        {board}
    </div>
}
