import { samePosition, PieceType, TeamType } from "../Constants";
import { pawnMove } from "./rules/PawnRules";
import { tileIsOccupied, tileIsEmptyOrOccupiedByOpponent } from "./rules/GeneralRules";
export default class Referee {
  isEnPassantMove(initialPosition, desiredPosition, type, team, boardState) {
    const pawnDirection = team === TeamType.OUR ? 1 : -1;

    // upper left or upper right || bottom left or bottom right
    // if a piece is under or above the attacked tile

    if (type == PieceType.PAWN) {
      if ((desiredPosition.x - initialPosition.x === -1 || desiredPosition.x - initialPosition.x === 1) && desiredPosition.y - initialPosition.y === pawnDirection) {
        const piece = boardState.find(p => p.position.x === desiredPosition.x && p.position.y === desiredPosition.y - pawnDirection && p.enPassant);
        if (piece) {
          return true;
        }
      }
    }
    return false;
  }

  // Bishop Function
  bishopMove(initialPosition, desiredPosition, type, team, boardState) {
    // BISHOP MOVING AND ATTACK LOGIC

    for (let i = 1; i < 8; i++) {
      // diagonal 
      let multiplierX = desiredPosition.x < initialPosition.x ? -1 : 1;
      let multiplierY = desiredPosition.y < initialPosition.y ? -1 : 1;
      let passedPosition = {
        x: initialPosition.x + i * multiplierX,
        y: initialPosition.y + i * multiplierY
      };
      if (samePosition(passedPosition, desiredPosition)) {
        return tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
      } else {
        if (tileIsOccupied(passedPosition, boardState)) {
          break;
        }
      }
    }
    return false;
  }

