import { PassThrough } from "stream";
import { samePosition, PieceType, TeamType , Piece, Position} from "../Constants";
import { pawnMove } from "./rules/PawnRules";

export default class Referee {
    tileIsEmptyOrOccupiedByOpponent(position: Position, boardState: Piece[], team: TeamType): boolean{
        return !this.tileIsOccupied(position, boardState) || this.tileIsOccupiedByOpponent(position, boardState, team);
    }
    tileIsOccupied(position: Position, boardState: Piece[]): boolean {
        const piece = boardState.find(p => p.position.x === position.x && p.position.y === position.y);
        if(piece){
            return true;
        }
        else {
            return false;
        }
    }

    tileIsOccupiedByOpponent(position: Position, boardState: Piece[], team: TeamType): boolean {
        const piece = boardState.find(p => p.position.x === position.x && p.position.y === position.y && p.team !== team);
        if (piece) {
            return true;
        }
        else{
            return false;
        }
    }

    isEnPassantMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]){
        const pawnDirection = (team === TeamType.OUR) ? 1: -1;

        // upper left or upper right || bottom left or bottom right
        // if a piece is under or above the attacked tile

        if (type == PieceType.PAWN){
            if ((desiredPosition.x - initialPosition.x === -1 || desiredPosition.x - initialPosition.x === 1)&& desiredPosition.y - initialPosition.y === pawnDirection){
                const piece = boardState.find(p => p.position.x === desiredPosition.x && p.position.y === desiredPosition.y - pawnDirection && p.enPassant);
                if(piece){
                    return true;
                }
            }

        }

       return false;
    }

    // pawnMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]){
    //     const specialRow = team === TeamType.OUR ? 1 : 6;
    //     const pawnDirection = team === TeamType.OUR ? 1 : -1;

    //     // Forward movement
    //     if (initialPosition.y === specialRow && desiredPosition.y - initialPosition.y === 2 * pawnDirection) {
    //         return !this.tileIsOccupied(desiredPosition, boardState) && !this.tileIsOccupied({ x: desiredPosition.x, y: desiredPosition.y - pawnDirection }, boardState);
    //     }

    //     // Normal forward movement
    //     if (desiredPosition.x === initialPosition.x && (desiredPosition.y - initialPosition.y === pawnDirection)) {
    //         return !this.tileIsOccupied(desiredPosition, boardState);
    //     }

    //     // Taking a piece diagonally
    //     if (Math.abs(desiredPosition.x - initialPosition.x) === 1 && desiredPosition.y - initialPosition.y === pawnDirection) {
    //         return this.tileIsOccupiedByOpponent(desiredPosition, boardState, team);
    //     }

    //     return false;
    // }



    // Knight Function
    knightMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {
        // KNIGHT MOVING LOGIC
        // 8 Different moving patterns
        for(let i = -1; i < 2; i+=2){
            for(let j = -1; j < 2; j+=2){
                if (desiredPosition.y - initialPosition.y === i * 2){
                    if (desiredPosition.x - initialPosition.x === j){
                        return this.tileIsEmptyOrOccupiedByOpponent(desiredPosition, boardState, team);

                    }
                }
                else if (desiredPosition.x - initialPosition.x === i * 2){
                    if(desiredPosition.y - initialPosition.y === j){
                        return this.tileIsEmptyOrOccupiedByOpponent(desiredPosition, boardState, team);
                    }
                }
            }
        }
    }

    // Bishop Function
    bishopMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {
        // BISHOP MOVING AND ATTACK LOGIC

        for (let i = 1; i < 8; i++){

            // diagonal 
            let multiplierX = (desiredPosition.x < initialPosition.x) ? -1 : 1;
            let multiplierY = (desiredPosition.y < initialPosition.y) ? -1 : 1;

            let passedPosition: Position = {x: initialPosition.x + (i * multiplierX), y: initialPosition.y + (i * multiplierY)};

            if(samePosition(passedPosition, desiredPosition)){
                return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
            }
            else{
                if(this.tileIsOccupied(passedPosition, boardState)){
                    break;
                }
            }
        }

        return false;
    }

    // Rook function
    rookMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {
        // ROOK MOVING AND ATTACK LOGIC
        for (let i = 1; i < 8; i++){
            // Vertical Movement
            let passedPosition;
            if(initialPosition.x === desiredPosition.x){
                let multiplier = (desiredPosition.y < initialPosition.y) ? -1 : 1;
                passedPosition = {x: initialPosition.x, y: initialPosition.y + (i * multiplier)};
            }
            // Horizontal
            else if(initialPosition.y === desiredPosition.y){
                let multiplier = (desiredPosition.x < initialPosition.x) ? -1 : 1;
                passedPosition= {x: initialPosition.x + (i * multiplier), y: initialPosition.y};

            }
            if(passedPosition) {
                if (samePosition(passedPosition, desiredPosition)) {
                    return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
                }
                else if (this.tileIsOccupied(passedPosition, boardState)){
                    break;
                }
            }
        }

        return false;

    }

    queenMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {
        // if (desiredPosition.x === initialPosition.x || desiredPosition.y === initialPosition.y){
        //     return this.rookMove(initialPosition, desiredPosition, type, team, boardState);
        // }
        // else{
        //     return this.bishopMove(initialPosition, desiredPosition, type, team, boardState);

        // }
        for(let i = 1; i < 8; i++){
            const multiplierX = desiredPosition.x < initialPosition.x ? -1 : desiredPosition.x > initialPosition.x ? 1 : 0;

            const multiplierY = desiredPosition.y < initialPosition.y ? -1 : desiredPosition.y > initialPosition.y ? 1 : 0;

            let passedPosition: Position = {x: initialPosition.x + (i * multiplierX), y: initialPosition.y + (i * multiplierY)};

            if(samePosition(passedPosition, desiredPosition)){
                return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
            }
            else{
                if(this.tileIsOccupied(passedPosition, boardState)){
                    break;
                }
            }
        }

        return false;
    }

    kingMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {
        // Basic king movement
        const multiplierX = desiredPosition.x < initialPosition.x ? -1 : desiredPosition.x > initialPosition.x ? 1 : 0;

        const multiplierY = desiredPosition.y < initialPosition.y ? -1 : desiredPosition.y > initialPosition.y ? 1 : 0;

        let passedPosition: Position = {x: initialPosition.x + (1 * multiplierX), y: initialPosition.y + (1 * multiplierY)};

        if(samePosition(passedPosition, desiredPosition)){
            return this.tileIsEmptyOrOccupiedByOpponent(passedPosition, boardState, team);
        }

        return false;
    }

    // Valid Move Function
    isValidMove(initialPosition: Position, desiredPosition: Position, type: PieceType, team: TeamType, boardState: Piece[]) {

        switch(type) {
            case PieceType.PAWN:
                return pawnMove(initialPosition, desiredPosition, type, team, boardState);

                case PieceType.KNIGHT:
                return this.knightMove(initialPosition, desiredPosition, type, team, boardState);

            case PieceType.BISHOP:
                return this.bishopMove(initialPosition, desiredPosition, type, team, boardState);

            case PieceType.ROOK:
                return this.rookMove(initialPosition, desiredPosition, type, team, boardState);

            case PieceType.QUEEN:
                return this.queenMove(initialPosition, desiredPosition, type, team, boardState);

            case PieceType.KING:
                return this.kingMove(initialPosition, desiredPosition, type, team, boardState);
        }


        return false;
    }
}
