import { PieceType, TeamType , Piece, Position} from "../Constants";

export default class Referee {

    tileIsOccupied(x: number, y: number, boardState: Piece[]): boolean {
        // console.log("Checking if tile is occupied");
        const piece = boardState.find(p => p.position.x === x && p.position.y === y);
        if(piece){
            return true;
        }
        else {
            return false;
        }
    }

    tileIsOccupiedByOpponent(x: number, y: number, boardState: Piece[], team: TeamType): boolean {
        const piece = boardState.find(p => p.position.x === x && p.position.y === y && p.team !== team);
        if (piece) {
            return true;
        }
        else{
            return false;
        }
    }

    isEnPassantMove(p: Position, x: number, y: number, type: PieceType, team: TeamType, boardState: Piece[]){
        const pawnDirection = (team === TeamType.OUR) ? 1: -1;

        // upper left or upper right || bottom left or bottom right
        // if a piece is under or above the attacked tile

        if (type == PieceType.PAWN){
            if ((x - p.x === -1 || x - p.x === 1)&& y - p.y === pawnDirection){
                const piece = boardState.find(p => p.position.x === x && p.position.y === y - pawnDirection && p.enPassant);
                if(piece){
                    return true;
                }
            }

        }

       return false;
    }

    isValidMove(initialPosition: Position, x: number, y: number, type: PieceType, team: TeamType, boardState: Piece[]) {
        // console.log("Ref is checking move");
        // console.log(`"Previous Location: ${px} ${py}`);
        // console.log(`"Current Location: ${x} ${y}`);
        // console.log(`Piece Type: ${type}`);
        // console.log(`Team: ${team}`);

        // pawn movement 
        if (type === PieceType.PAWN){
            const specialRow = (team === TeamType.OUR) ? 1: 6;
            const pawnDirection = (team === TeamType.OUR) ? 1: -1;

            // forward movement
            if(p.y === specialRow && y - p.y === 2 *pawnDirection){
                if(!this.tileIsOccupied(x, y, boardState) && !this.tileIsOccupied(x, y - pawnDirection, boardState)){
                        return true;
                }
            }
            else if(p.x === x && (y - p.y === pawnDirection)){
                    if(!this.tileIsOccupied(x, y, boardState)){
                        return true;
                    }
            }

            // taking a piece
            else if (x - p.x === -1 && y - p.y === pawnDirection){
                // attack in upper or bottom left corner
                if(this.tileIsOccupiedByOpponent(x, y, boardState, team)){
                    return true;
                }
            }
            else if (x - p.x === 1 && y - p.y === pawnDirection){
                // attack in upper or bottom right corner
                if(this.tileIsOccupiedByOpponent(x, y, boardState, team)){
                    return true;
                }
            }
        }


        return false;
    }
}
